import React, { useEffect, useState } from 'react';
// useState se usa para asignar un valor a nuestras variables
// useEffect se usa para cuando se renderiza la página
import axios from 'axios';
// axios se usa para hacer peticiones a la API
import Swal from 'sweetalert2';
// sweetalert2 se usa para mostrar alertas en la página
import withReactContent from 'sweetalert2-react-content';
// withReactContent se usa para mostrar alertas en la página
import { Pie } from 'react-chartjs-2';
// Pie se usa para crear la gráfica circular
import { Chart, ArcElement, Tooltip, Legend } from 'chart.js';
// Chart, ArcElement, Tooltip y Legend se usan para registrar los componentes necesarios en Chart.js

Chart.register(ArcElement, Tooltip, Legend);

const MySwal = withReactContent(Swal);

const ShowProducts = () => {
    const url = 'http://127.0.0.1:9000/api';
    const [productos, setProductos] = useState([]);
    const [_id, setId] = useState('');
    const [producto, setProducto] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [precio, setPrecio] = useState('');
    const [cantidad, setCantidad] = useState('');
    const [operation, setOperation] = useState(1); // 1 = agregar, 2 = editar
    const [titulo, setTitulo] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Función para obtener los productos desde la API
    const getProducts = async () => {
        try {
            setLoading(true);
            const respuesta = await axios.get(url);
            setProductos(respuesta.data);
            setLoading(false);
        } catch (err) {
            setError(err);
            setLoading(false);
        }
    };

    useEffect(() => {
        getProducts();
    }, []);

    // Función para mostrar alertas
    const show_alert = (mensaje, tipo) => {
        MySwal.fire({
            title: tipo === 'Éxito' ? 'Éxito' : 'Error',
            text: mensaje,
            icon: tipo.toLowerCase(),
            confirmButtonText: 'OK'
        });
    };

    // Función para eliminar productos
    const borrarProducto = (id) => {
        MySwal.fire({
            title: '¿Estás seguro de eliminar este producto?',
            icon: 'question',
            text: 'No se podrá dar marcha atrás',
            showCancelButton: true,
            confirmButtonText: 'Sí, eliminar',
            cancelButtonText: 'Cancelar'
        }).then((result) => {
            if (result.isConfirmed) {
                axios.delete(`${url}/${id}`)
                    .then(() => {
                        setProductos(productos.filter(producto => producto._id !== id));
                        show_alert('Producto eliminado correctamente', 'success');
                    })
                    .catch(error => {
                        console.error('Error deleting product:', error);
                        show_alert('Hubo un error al eliminar el producto', 'error');
                    });
            }
        });
    };

    // Función para agregar o editar productos
    const guardarProducto = () => {
        const productoData = { producto, descripcion, precio, cantidad };
        if (operation === 1) {
            // Agregar producto
            axios.post(url, productoData)
                .then((response) => {
                    setProductos([...productos, response.data]);
                    show_alert('Producto agregado correctamente', 'success');
                })
                .catch(error => {
                    console.error('Error adding product:', error);
                    show_alert('Hubo un error al agregar el producto', 'error');
                });
        } else {
            // Editar producto
            axios.put(`${url}/${_id}`, productoData)
                .then((response) => {
                    setProductos(productos.map(producto => producto._id === _id ? response.data : producto));
                    show_alert('Producto editado correctamente', 'success');
                })
                .catch(error => {
                    console.error('Error editing product:', error);
                    show_alert('Hubo un error al editar el producto', 'error');
                });
        }
    };

    return (
        <div>
            {/* Renderiza tus productos aquí */}
            {loading ? <p>Cargando...</p> : (
                <ul>
                    {productos.map(producto => (
                        <li key={producto._id}>
                            {producto.nombre} - {producto.descripcion}
                            <button onClick={() => borrarProducto(producto._id)}>Eliminar</button>
                            <button onClick={() => {
                                setId(producto._id);
                                setProducto(producto.nombre);
                                setDescripcion(producto.descripcion);
                                setPrecio(producto.precio);
                                setCantidad(producto.cantidad);
                                setOperation(2);
                                setTitulo('Editar Producto');
                            }}>Editar</button>
                        </li>
                    ))}
                </ul>
            )}
            {error && <p>Error: {error.message}</p>}
            {/* Formulario para agregar/editar productos */}
            <form onSubmit={(e) => {
                e.preventDefault();
                guardarProducto();
            }}>
                <input type="text" value={producto} onChange={(e) => setProducto(e.target.value)} placeholder="Producto" />
                <input type="text" value={descripcion} onChange={(e) => setDescripcion(e.target.value)} placeholder="Descripción" />
                <input type="number" value={precio} onChange={(e) => setPrecio(e.target.value)} placeholder="Precio" />
                <input type="number" value={cantidad} onChange={(e) => setCantidad(e.target.value)} placeholder="Cantidad" />
                <button type="submit">{operation === 1 ? 'Agregar' : 'Guardar'}</button>
            </form>

            {/* Tabla de productos */}
            <div className="row mt-3">
                <div className="col-12">
                    <div className="table-responsive">
                        <table className="table table-bordered">
                            <thead>
                                <tr>
                                    <th>Producto</th>
                                    <th>Descripción</th>
                                    <th>Precio</th>
                                    <th>Cantidad en Stock</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {productos.map(producto => (
                                    <tr key={producto._id}>
                                        <td>{producto.producto}</td>
                                        <td>{producto.descripcion}</td>
                                        <td>${new Intl.NumberFormat('es-GT').format(producto.precio)}</td>
                                        <td>{producto.cantidadEnStock}</td>
                                        <td>
                                            <button onClick={() => {
                                                setId(producto._id);
                                                setProducto(producto.producto);
                                                setDescripcion(producto.descripcion);
                                                setPrecio(producto.precio);
                                                setCantidad(producto.cantidadEnStock);
                                                setOperation(2);
                                                setTitulo('Editar Producto');
                                            }} className='btn btn-warning' data-bs-toggle='modal' data-bs-target='#modalProducts'>
                                                <i className='fa-solid fa-edit'> Editar </i>
                                            </button>
                                            <button onClick={() => borrarProducto(producto._id)} className='btn btn-danger ms-2'>
                                                <i className='fa-solid fa-trash'> Eliminar </i>
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            {/* Gráfica circular para mostrar el total de productos */}
            <div className="row mt-3">
                <div className='col-12 col-lg-8 offset-0 offset-lg-2'>
                    <div className='d-flex justify-content-center'>
                        <Pie data={pieData} />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ShowProducts;
